import Swal from 'sweetalert2';

function Reminder() {
    const [receiverEmail, setReceiverEmail] = useState('');
    return (
        <MainLayout>
            <div className="max-w-lg bg-white shadow-lg mx-auto p-7 rounded mt-6">
                <h2 className="font-semibold text-xl mb-4 text-center block">Send Reminder</h2>
                <form className="space-y-2">
                    <div>
                        <label htmlFor="">Product Name</label>
                        <input type="text" value={receiverEmail} onChange={(e) => setName(e.target.value)} className="w-full block px-3 border text-gray-500 rounded focus:outline-none focus:shadow-outline focus:border-blue-200 space-y-3 my-2 placeholder-gray-400" placeholder="enter product name" />
                    </div>



                    <div>
                        {!isLoading && (
                            <button className="mt-5 block w-full bg-blue-700 text-white rounded-sm px-4 py-2 font-bold hover:bg-blue-600 hover:cursor-pointer">Save</button>
                        )}

                    </div>

                </form>
            </div>

        </MainLayout>
    );
}

export default Reminder;
