import React, { useState, useEffect } from 'react';
import MainLayout from '../layout/MainLayout';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Shop() {
    const [product, setProduct] = useState([]);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('https://node-backend-test.vercel.app/api/product/allproducts');
            setProduct(response.data);
            console.log(response.data);

        } catch (error) {
            console.log(error)

        }

    }

    useEffect(() => {
        fetchProducts()

    }, [])
    return (
        <MainLayout>
            <div className="grid grid-2-col lg:flex justify-start items-center my-5 px-4">
                {product.length > 0 ? (
                    <>
                        {product.map((item) => (

                            <div class="max-w-sm bg-white border border-gray-200 mx-3 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                                <a href="#">
                                    <img class="rounded-t-lg" src={item.image} alt="" />
                                </a>
                                <div class="p-5">
                                    <a href="#">
                                        <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{item.name}</h5>
                                    </a>
                                    <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">Here are the biggest enterprise technology acquisitions of 2021 so far, in reverse chronological order.</p>
                                    <Link to="" class="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">
                                        Update
                                        <svg class="rtl:rotate-180 w-3.5 h-3.5 ms-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 10">
                                            <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 5h12m0 0L9 1m4 4L9 9" />
                                        </svg>
                                    </Link>
                                </div>
                            </div>

                        ))}
                    </>
                ) : (
                    <p>No product found</p>
                )}


            </div>
        </MainLayout>
    );
}

export default Shop;
