import React, { useState, useEffect } from 'react';
import MainLayout from '../layout/MainLayout';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
//import { toast } from 'react-toastify';

function Shop() {
    const [product, setProduct] = useState([]);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('https://node-backend-test.vercel.app/api/product/allproducts');
            setProduct(response.data);
            console.log(response.data);

        } catch (error) {
            console.log(error)

        }

    }

    useEffect(() => {
        fetchProducts()

    }, [])


    //delete product
    const deleteProduct = () =>{
        toast.error("Delete clicked")

    }
    return (
        <MainLayout>
            <div>
                    <Link to="/create" class="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-green-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-green-600 dark:hover:bg-green-700 dark:focus:ring-green-800">
                        Add Product
                        <svg class="rtl:rotate-180 w-3.5 h-3.5 ms-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 10">
                            <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 5h12m0 0L9 1m4 4L9 9" />
                        </svg>
                    </Link>
                </div>
            <div className="grid grid-cols-2 lg:grid-cols-4 flex justify-start items-center my-5 px-4">

                {product.length > 0 ? (
                    <>
                        {product.map((item,index) => (

                            <div key={index} class="max-w-sm max-h-lg bg-white border border-gray-200 mx-3 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                                <Link to="#">
                                    <img class="rounded-t-lg" src={item.image} alt="" />
                                </Link>
                                <div class="p-5">
                                    <Link to="#">
                                        <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{item.name}</h5>
                                    </Link>
                                    <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">Kshs.{item.price}</p>
                                    <div className="flex mt-2 gap-4">
                                        <button onClick={() => deleteProduct} class="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-red-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-red-600 dark:hover:bg-red-700 dark:focus:ring-red-800">
                                            Delete
                                            <svg class="rtl:rotate-180 w-3.5 h-3.5 ms-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 10">
                                                <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 5h12m0 0L9 1m4 4L9 9" />
                                            </svg>
                                        </button>
                                        <Link to={`/edit/${item._id}`} class="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">
                                            Edit
                                            <svg class="rtl:rotate-180 w-3.5 h-3.5 ms-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 10">
                                                <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 5h12m0 0L9 1m4 4L9 9" />
                                            </svg>
                                        </Link>
                                    </div>
                                </div>
                            </div>

                        ))}
                    </>
                ) : (
                    <p>No product found</p>
                )}


            </div>
        </MainLayout>
    );
}

export default Shop;
